    //INCREASE AND DECREASE FOOD QUANTITY
    $('.quantity-minus').on('click', function () {
        if ($('.quantity-input').val() > 1) {
            $('.quantity-input').val(+ $('.quantity-input').val() - 1);
        }
    });

    $('.quantity-plus').on('click', function () {
        $('.quantity-input').val(+ $('.quantity-input').val() + 1);
    });

    //CHOOSE MEAL SIZE
